mapper.update:
UPDATE DEPOSIT_ORDER SET BANK_SERIAL_NO=#{bankSerialNo}, DEPOSIT_STATUS=#{depositStatus},
                DEPOSIT_MESSAGE=#{depositMessage}, RESPONSE_CODE=#{responseCode}, DEPOSIT_AT=#{depositAt}
WHERE DEPOSIT_FLOW_ID=#{depositFlowId}

mapper.select:
SELECT

FROM DEPOSIT_ORDER D
WHERE D.DEPOSIT_FLOW_ID=#{depositFlowId}
