mapper.update:
UPDATE WITHDRAW_ORDER SET WITHDRAW_STATUS=#{withdrawStatus}, WITHDRAW_MESSAGE=#{withdrawMessage},
                        RESPONSE_CODE=#{responseCode}, WITHDRAW_AT=#{withdrawAt}
WHERE WITHDRAW_FLOW_ID=#{withdrawFlowId}

UPDATE WITHDRAW_ORDER SET QUERY_STATUS=#{queryStatus}, QUERY_MESSAGE=#{queryMessage},
QUERY_RESPONSE_CODE=#{queryResponseCode}, QUERY_AT=#{queryAt}
WHERE WITHDRAW_FLOW_ID=#{withdrawFlowId}

mapper.select:
SELECT

FROM WITHDRAW_ORDER W
WHERE W.WITHDRAW_FLOW_ID=#{withdrawFlowId}

SELECT

FROM WITHDRAW_ORDER W
WHERE W.ORDER_ID=#{orderId}
